<script>
import { Zilliqa } from '@zilliqa-js/zilliqa';
import { mapGetters } from 'vuex';

export default {
  name: 'Login',
  data() {
    return {
      keystoreData: null,
      passphrase: null,
      zilliqa: null
    };
  },
  computed: {
    ...mapGetters('general', {
      network: 'selectedNetwork',
      isLogged: 'isLogged'
    })
  },
  mounted() {
    if (this.isLogged === true) {
      return this.$router.push({ name: 'welcome' });
    }

    this.zilliqa = new Zilliqa(this.network);
  },
  methods: {
    async loginWithKeystore() {
      EventBus.$emit('login-event', 'keystore');
    },
    loginWithLedger() {
      EventBus.$emit('login-event', 'ledger');
    },
    loginWithZilPay() {
      window.EventBus.$emit('login-event', 'zilpay');
    },
    openVideo() {
      EventBus.$emit('video-event');
    },
    openInfo() {}
  }
};
</script>

